import "./App.css";
import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes, useLocation, useParams, Link, useNavigate, Navigate } from 'react-router-dom';
import Cards from "./components/Cards";
import Nav from './components/Nav';
import About from './components/About';
import Detail from './components/Detail';
import Form from './components/Form';
import Favorites from "./components/Favorites.jsx";



function App() {
  const navigate = useNavigate(); 
  const [access, setAccess] = React.useState(false);
  const [errorApi, seterrorApi] = React.useState(false);


  async function login(userData) {
    const { email, password } = userData;
    const URL = "http://localhost:3001/user/login/";

    try {
      const backendLogin = await axios(
        URL + `?email=${email}&password=${password}`
      );
      const { data } = backendLogin;
      const { access } = data;
      setAccess(access);
      access && navigate("/home");
    } catch (error) {
    
      alert(error.message);
    }
  }

  useEffect(() => {
    !access && navigate("/");
  
  }, [access]);

  async function onSearch(id) {
 

    try {
      const backRequest = await axios(
        `http://localhost:3001/character/${id}`
      );
      if (backRequest.data.name) {
        seterrorApi(false);
        setCharacters((oldChars) => [...oldChars, backRequest.data]);
      } else {
        seterrorApi(true);
      }
    } catch (error) {
      seterrorApi(true);
    }
  }

  function onClose(id) {
    setCharacters(
      characters.filter((pj) => {
        return pj.id !== Number(id);
      })
    );
  }

  const [characters, setCharacters] = useState([]);

  const location = useLocation();
  return (
   
      <div className='App'>
        {pathname !== '/' && <Nav onSearch={onSearch} />}
     
        <Routes>
        <Route path="/" element={<Form Login={login} />} className="my-route" />
      <Route path="/favorites" element={<Favorites />} />
      <Route path="/about" element={<About />} />
      <Route path="/home" element={<Cards characters={characters} onClose={onClose} />} />
      <Route path="/detail/:id" element={<Detail />} />
    </Routes>
       
      </div>
    
  );
}

export default App;
