import "./App.css";
import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes, useLocation, useParams, Link, useNavigate, Navigate } from 'react-router-dom';
import Cards from "./components/Cards";
import Nav from './components/Nav';
import About from './components/About';
import Detail from './components/Detail';
import Form from './components/Form';
import Favorites from "./components/Favorites.jsx";



function App() {
  const [characters, setCharacters] = useState([]);
  const {pathname}= useLocation();
  const [access, setAccess] = React.useState(false);
  const [errorApi, seterrorApi] = React.useState(false);
  const navigate = useNavigate()
const EMAIL = 'ejemplo@gmail.com';
const PASSWORD = 'unaPassword';

async function login(userData) {
  const { email, password } = userData;
  const URL = "http://localhost:3001/user/login/";

  try {
    const backendLogin = await axios(
      URL + `?email=${email}&password=${password}`
    );
    const { data } = backendLogin;
    const { access } = data;
    setAccess(access);
    access && navigate("/home");
  } catch (error) {
   
    alert(error.message);
  }

}

function onSearch(id) {

  axios(`http://localhost:3001/rickandmorty/onsearch/${id}`)
    .then((respuesta) => {
      if (respuesta.data.name) {
       
        setCharacters((oldChars) => [...oldChars, respuesta.data]);
      } else {
      }
    })
    .catch((err) => alert(err.response.data.error));
}

  const onClose = (id) => {
    const parsedId = parseInt(id);
    const charactersFiltered = characters.filter((character) => character.id !== parsedId);
    setCharacters(charactersFiltered);
  };

  
  return (
   
      <div className='App'>
        {pathname !== '/' && <Nav onSearch={onSearch} />}
     
        <Routes>
        <Route path="/" element={<Form Login={login} />} className="my-route" />
      <Route path="/favorites" element={<Favorites />} />
      <Route path="/about" element={<About />} />
      <Route path="/home" element={<Cards characters={characters} onClose={onClose} />} />
      <Route path="/detail/:id" element={<Detail />} />
    </Routes>
       
      </div>
    
  );
}

export default App;
