import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const Detail = () => {
  const { id } = useParams();
  const [pjDetail, setpjDetail] = useState({});

  useEffect(() => {
    axios(`http://localhost:3001/rickandmorty/character/${id}`)
      .then((response ) => {
        
        if (response.data.name) {
          setpjDetail(response.data);
        } else {
          
          window.alert('No hay personajes con ese ID');
        }
      })
      .catch((error) => {
        console.log(error);
        window.alert('Error al obtener los datos del personaje');
      });
  }, [id]);

  return (
    <div>
      <h3>{pjDetail.name && pjDetail.name}</h3>
      <h5>{pjDetail.status ? pjDetail.status : ":( no hay status"}</h5>
      <img src={pjDetail.image} alt={pjDetail.name} />
      <section>
        <span>üêû{pjDetail.species}</span>
        <span> ‚ôÄ {pjDetail.gender}</span>
        <span>üåç {pjDetail.origin?.name}</span>
      </section>
    </div>
  );
};

export default Detail;
