import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  useLocation,
  useNavigate
} from "react-router-dom";
import Cards from "./components/Cards";
import Nav from "./components/Nav";
import About from "./components/About";
import Detail from "./components/Detail";
import Form from "./components/Form";
import Favorites from "./components/Favorites.jsx";

function App() {
  const navigate = useNavigate();
  const [access, setAccess] = useState(false);
  const [errorApi, seterrorApi] = useState(false);

  useEffect(() => {
    !access && navigate("/");
  }, [access]);

  async function login(userData) {
    const { email, password } = userData;
    const URL = "http://localhost:3001/user/login/";

    try {
      const backendLogin = await axios(URL + `?email=${email}&password=${password}`);
      const { data } = backendLogin;
      const { access } = data;
      setAccess(access);
      access && navigate("/home");
    } catch (error) {
      alert(error.message);
    }
  }

  async function onSearch(dato) {
    try {
      const backRequest = await axios(`http://localhost:3001/character/${dato}`);
      if (backRequest.data.name) {
        seterrorApi(false);
        setCharacters((oldChars) => [...oldChars, backRequest.data]);
      } else {
        seterrorApi(true);
      }
    } catch (error) {
      seterrorApi(true);
    }
  }

  function onClose(id) {
    setCharacters((oldChars) => oldChars.filter((pj) => pj.id !== Number(id)));
  }

  const [characters, setCharacters] = useState([]);
  const location = useLocation();
  return (
   
      <div className="App">
        {location.pathname !== "/" && <Nav onSearch={onSearch} />}

        <Routes>
          <Route path="/" element={<Form Login={login} />} className="my-route" />
          <Route path="/favorites" element={<Favorites />} />
          <Route path="/about" element={<About />} />
          <Route path="/home" element={<Cards characters={characters} onClose={onClose} />} />
          <Route path="/detail/:id" element={<Detail />} />
        </Routes>
      </div>
   
  );
}

export default App;
